package string;

import java.util.HashMap;

public class NumSplits {
    public static int numSplits(String s) {
       int ans = 0;
       HashMap<Character, Integer> left = new HashMap<>(), right = new HashMap<>();

       for (char c : s.toCharArray()) right.put(c, right.getOrDefault(c, 0) + 1);

       for (int i = 0; i < s.length(); i++) {

           left.put(s.charAt(i), left.getOrDefault(s.charAt(i), 0) + 1);
           right.put(s.charAt(i), right.get(s.charAt(i)) - 1);

           if (right.get(s.charAt(i)) == 0) right.remove(s.charAt(i));

           if (left.size() == right.size()) ans++;
       }

       return ans;
    }

    public static void main(String[] args) {
        System.out.println(numSplits(""));
    }
}
